import { StatusBar } from 'expo-status-bar';
import { Button, StyleSheet, Text, View, TextInput } from 'react-native';
import { Client } from './Models/Client'
import { useEffect, useState } from 'react';
import Menu from './components/menu/Menu';
import ViewClients from './components/ViewClients/ViewClients';
const testClient = new Client("RydgeSoftware", "kaydon stubbs", "4352622247", "test@gmail.com", "English", Date.now(), "Febuary,20,2024", "BiWeekly")
export default function App() {
  const [clients, setClients] = useState([testClient])
  const [addClient, setAddClient] = useState(false)
  const [viewClients, setViewClients] = useState(false)


  const handlAdd = (e) => {
    let client = new Client(clientName, contactName, contactPhone, contactEmail, contactLanguage, Date.now(), endTime, billPeriod)
    if (client.clientName != "") {
      alert(client.clientName)
      let c = clients;
      c.push(client);
      setClients(c);
      clearNewContactInfo()
      setAddClient(false)
    }
    else {
      alert("Client info missing. Client will not be added")
      setAddClient(false)
    }

  }
  const handlAddClient = (e) => {
    setAddClient(true)
    setViewClients(false)
  }
  const handlViewClient = (e) => {
    setAddClient(false)
    setViewClients(true)
  }
  function clearNewContactInfo() {
    setClientName("");
    setContactName("");
    setContactPhone("")
    setContactEmail("");
    setContactLanguage("")
    setEndTime("")
    setBillPeriod("")
  }
  return (
    <View style={styles.container}>
      <Menu addClient={handlAddClient} viewClients={handlViewClient}></Menu>
      {clients.length > 0 && viewClients&&
        <ViewClients clients={clients}></ViewClients>
      }{addClient && <>
        <TextInput
          style={styles.input}
          onChangeText={setClientName}
          name="clientName"
          value={clientName || ""}
        />
        <TextInput
          style={styles.input}
          onChangeText={setContactName}
          name="contactName"
          value={contactName || ""}
        />
        <TextInput
          style={styles.input}
          onChangeText={setContactPhone}
          name="contactPhone"
          value={contactPhone || ""}
        />
        <TextInput
          style={styles.input}
          onChangeText={setContactEmail}
          name="contactEmail"
          value={contactEmail || ""}
        />
        <TextInput
          style={styles.input}
          onChangeText={setContactLanguage}
          name="contactLanguage"
          value={contactLanguage || ""}
        />
        <TextInput
          style={styles.input}
          onChangeText={setEndTime}
          name="endTime"
          value={endTime || ""}
        />
        <TextInput
          style={styles.input}
          onChangeText={setBillPeriod}
          name="billPeriod"
          value={billPeriod || ""}
        />
        <Button onPress={handlAdd} title="Add Client Info"></Button>
      </>
      }



      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
  },
});
