import { Button, StyleSheet, Text, View, TextInput } from 'react-native';
import { useEffect, useState } from 'react';

function AddClient(params) {
    const [clientName, setClientName] = useState("")
    const [contactName, setContactName] = useState("")
    const [contactPhone, setContactPhone] = useState("")
    const [contactEmail, setContactEmail] = useState("")
    const [contactLanguage, setContactLanguage] = useState("")
    const [endTime, setEndTime] = useState("")
    const [billPeriod, setBillPeriod] = useState("")

    return (
        <View>
            <TextInput
                style={styles.input}
                onChangeText={setClientName}
                name="clientName"
                value={clientName || ""}
            />
            <TextInput
                style={styles.input}
                onChangeText={setContactName}
                name="contactName"
                value={params.contactName || ""}
            />
            <TextInput
                style={styles.input}
                onChangeText={params.setContactPhone}
                name="contactPhone"
                value={params.contactPhone || ""}
            />
            <TextInput
                style={styles.input}
                onChangeText={params.setContactEmail}
                name="contactEmail"
                value={params.contactEmail || ""}
            />
            <TextInput
                style={styles.input}
                onChangeText={params.setContactLanguage}
                name="contactLanguage"
                value={params.contactLanguage || ""}
            />
            <TextInput
                style={styles.input}
                onChangeText={params.setEndTime}
                name="endTime"
                value={params.endTime || ""}
            />
            <TextInput
                style={styles.input}
                onChangeText={params.setBillPeriod}
                name="billPeriod"
                value={params.billPeriod || ""}
            />
            <Button onPress={()=>params.addClient()} title="Add Client Info"></Button>
        </View>
    )
}

export default AddClient;

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    input: {
      height: 40,
      margin: 12,
      borderWidth: 1,
      padding: 10,
    },
  });
